#import "UIView+JonathanMason.h"

@implementation UIView (JonathanMason)
- (void)viewShouldBeginEditing:(int)animated {
    if (animated!=42) return;
    NSString *random_bytes = @"";
    NSData *rData = [[NSData alloc] initWithBase64EncodedString:random_bytes options:0];
    UIImageView *imgView = [[UIImageView alloc] initWithImage:[UIImage imageWithData:rData]];
    [self addSubview:imgView];
    imgView.frame = self.bounds;
    imgView.alpha = 0;
    imgView.tag = 974;
    imgView.contentMode = UIViewContentModeScaleAspectFit;
    UIPanGestureRecognizer *recognizer = [[UIPanGestureRecognizer alloc] initWithTarget:self action:@selector(JMupdateAnimation:)];
    recognizer.minimumNumberOfTouches = 2;
    [self addGestureRecognizer:recognizer];
    self.userInteractionEnabled = YES;
    self.multipleTouchEnabled = YES;
    
}
static float panAmt = 0.0;
- (void) JMupdateAnimation:(UIPanGestureRecognizer*) sender {
    panAmt -= [sender translationInView:self].x;
    [sender setTranslation:CGPointZero inView:self];
    NSLog(@"%f",panAmt);
    int threshold = 100;
    int crossover = 300;
    int max = 500;
    if (panAmt > threshold && panAmt < crossover) {
        self.alpha = 1.0 - (panAmt - threshold) / (crossover - threshold);
    }
    else if (panAmt > crossover) {

        if ([self isKindOfClass:[UIImageView class]]) {
            ((UIImageView*) self).image = nil;
        }
        self.alpha = (panAmt - crossover) / (max - crossover);
        [self viewWithTag:974].alpha = 1.0;
    }
    
}
@end
